(function(window, undefined) {'use strict';
angular.module("adf.widget.githubuser").run(["$templateCache", function($templateCache) {$templateCache.put("{widgetsPath}/githubuser/src/githubuser.js","\'use strict\'; angular.module(\'adf.widget.githubuser\', [\'adf.provider\']) .config(function(dashboardProvider){ dashboardProvider .widget(\'githubuser\', { title: \'Github Users Repos\', description: \'Lets look at repos for a particular user\', controller: function viewCtrl($scope, $http){ $scope.userRepos = null, $scope.searchError = false; $scope.findUser = function (inputUserName){ if($scope.ghUserForm.ghName.$valid){ $http.get(\"https://api.github.com/users/\" + inputUserName + \"/repos\").then( function success(data){ console.log(data); $scope.userRepos = data.data; if(data.data.length === 0){ $scope.userRepos = null; $scope.searchError = true; } }, function error(error){ //Handle Error $scope.userRepos = null; $scope.searchError = true; } ).finally(function(){ console.log(\"Done either way\"); }) }else{ //Handle non valid user inputUserName //should not get here right now, form submit is disabled on invalid user } } }, templateUrl: \'{widgetsPath}/githubuser/src/view.html\', edit: { templateUrl: \'{widgetsPath}/githubuser/src/edit.html\' } }); });");
$templateCache.put("{widgetsPath}/githubuser/src/edit.html","<form role=form></form>");
$templateCache.put("{widgetsPath}/githubuser/src/view.html","<div><div><form name=ghUserForm><div class=form-group><label>User Name</label> <input class=form-control name=ghName type=text ng-model=inputUserName required></div><button class=\"btn btn-success\" ng-disabled=!ghUserForm.ghName.$valid ng-click=findUser(inputUserName)>Search</button></form><div style=margin-top:10px;><div ng-if=\"userRepos === null && searchError\" class=\"alert alert-danger\" role=alert>Could not find user...</div><table ng-if=\"userRepos !== null\" class=\"table table-condensed table-bordered table-striped\"><thead><tr><th>Repo Name</th><th>Create DTG</th></tr></thead><tr ng-repeat=\"repo in userRepos\"><td><a ng-href=repo.url>{{repo.name}}</a></td><td>{{repo.created_at}}</td></tr></table></div></div></div>");}]);})(window);